$(()=>{$("#form-search").submit(()=>{const t=$("#form-search").serialize();$("#table-aluno").html("<h1>Carregando...</h1>");$.ajax({url:"/alunos/search",method:"get",contentType:"application/json",data:t}).done(t=>{$("#table-aluno").html('<thead> <tr> <th scope="col">#</th><th scope="col">Nome</th> <th scope="col">Turma</th><th scope="col">Opções</th></tr></thead>');for(let a=0;a<=t.length;a++)$("#table-aluno").append("<tr>").append("<td>"+t[a].AL_ID+"</td>").append("<td>"+t[a].AL_NAME+"</td>").append("<td>"+t[a].turma.TR_NAME+"</td>").append('<td><a href="/alunos/'+t[a].AL_ID+'"><button class="btn btn-sm btn-warning">Editar</button></a><a href="/alunos/delete/'+t[a].AL_ID+' "><button class="btn btn-sm btn-danger">Excluir</button></a> </td>').append("</tr>")}).fail(t=>{console.log("Ocorreu um erro: "+t.status)});return!1})});